mapper.insert:
insert into BTH_MCHT_LIST_TEMP_INFO (MCHT_ID, CHK_DATE, STATUS
  )
values (#{mchtId,jdbcType=VARCHAR}, #{chkDate,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}
  )

mapper.update:
update BTH_MCHT_LIST_TEMP_INFO
set CHK_DATE = #{chkDate,jdbcType=VARCHAR},
  STATUS = #{status,jdbcType=VARCHAR},
  CRT_TM = #{crtTm,jdbcType=TIMESTAMP},
  LAST_UPD_TM = #{lastUpdTm,jdbcType=TIMESTAMP}
where MCHT_ID = #{mchtId,jdbcType=VARCHAR}

mapper.select:
select * FROM
(
select a.*,rownum rn from
(
select

from BTH_MCHT_LIST_TEMP_INFO
order by MCHT_ID
) a where rownum <= #{maxIndex}
) WHERE rn >= #{minIndex}
