mapper.select:
select 

from BTH_PAGY_CHK_INF
where PAGY_NO = #{pagyNo,jdbcType=CHAR}
  and CHK_DATA_DT = #{chkDataDt,jdbcType=TIMESTAMP}

mapper.delete:
delete from BTH_PAGY_CHK_INF
where PAGY_NO = #{pagyNo,jdbcType=CHAR}
  and CHK_DATA_DT = #{chkDataDt,jdbcType=TIMESTAMP}

mapper.select:
select

from BTH_PAGY_CHK_INF
where PAGY_NO IN  (#{pagyNo1,jdbcType=CHAR} , #{pagyNo2,jdbcType=CHAR})
and to_char(CHK_DATA_DT,'yyyymmdd') = #{settleDate,jdbcType=VARCHAR}
and GET_CHK_ST = #{getChkSt,jdbcType=CHAR}
