mapper.select:
select 

from BTH_UNION_FILE_DET
where PROXY_INS_CODE = #{proxyInsCode,jdbcType=VARCHAR}
  and SEND_INS_CODE = #{sendInsCode,jdbcType=VARCHAR}
  and TRACE_NUM = #{traceNum,jdbcType=VARCHAR}
  and TRANS_DATE = #{transDate,jdbcType=VARCHAR}

mapper.delete:
delete from BTH_UNION_FILE_DET
where PROXY_INS_CODE = #{proxyInsCode,jdbcType=VARCHAR}
  and SEND_INS_CODE = #{sendInsCode,jdbcType=VARCHAR}
  and TRACE_NUM = #{traceNum,jdbcType=VARCHAR}
  and TRANS_DATE = #{transDate,jdbcType=VARCHAR}

