mapper.delete:
delete from IFS_STAFF_ROLE_REL
where TLR_ID = #{tlrId,jdbcType=VARCHAR}
  and ROLE_ID = #{roleId,jdbcType=VARCHAR}
  and BR_ID = #{brId,jdbcType=VARCHAR}

mapper.insert:
insert into IFS_STAFF_ROLE_REL (TLR_ID, ROLE_ID, BR_ID,FLAG
  )
values (#{tlrId,jdbcType=VARCHAR}, #{roleId,jdbcType=VARCHAR}, #{brId,jdbcType=VARCHAR},
 #{flag,jdbcType=CHAR}
  )

mapper.update:
update IFS_STAFF_ROLE_REL


    TLR_ID = #{tlrId,jdbcType=VARCHAR},


    ROLE_ID = #{roleId,jdbcType=VARCHAR},


    BR_ID = #{brId,jdbcType=VARCHAR},


    FLAG = #{flag,jdbcType=CHAR},


where TLR_ID = #{tlrId,jdbcType=VARCHAR}
  and ROLE_ID = #{roleId,jdbcType=VARCHAR}
  and BR_ID = #{brId,jdbcType=VARCHAR}

mapper.select:
select 
TLR_ID,ROLE_ID,BR_ID
from IFS_STAFF_ROLE_REL
where TLR_ID = #{tlrId,jdbcType=VARCHAR}
and BR_ID = #{brId,jdbcType=VARCHAR}
