mapper.select:
select 

from KEEP_ACCT_INFO
where ORDER_SSN = #{orderSsn,jdbcType=VARCHAR}
  and SUB_ORDER_SSN = #{subOrderSsn,jdbcType=VARCHAR}
  and ORDER_SEQ = #{orderSeq,jdbcType=CHAR}
  and KEEP_TYPE = #{keepType,jdbcType=CHAR}

mapper.delete:
delete from KEEP_ACCT_INFO
where ORDER_SSN = #{orderSsn,jdbcType=VARCHAR}
  and SUB_ORDER_SSN = #{subOrderSsn,jdbcType=VARCHAR}
  and ORDER_SEQ = #{orderSeq,jdbcType=CHAR}
  and KEEP_TYPE = #{keepType,jdbcType=CHAR}

mapper.update:
update KEEP_ACCT_INFO
set MISC1 = #{merg,jdbcType=VARCHAR}
where mcht_id = #{mchtId,jdbcType=VARCHAR}
