mapper.select:
select MCHT_ID,MCHT_NAME,MCHT_SIM_NAME,mcht_State,MCHT_TYPE,MCHT_NAT,PAR_MCH_ID,EXAM_PS,EMAIL,aud.AUDIT_STATE EXAM_RESULT,aud.audit_datet_ime audit_Time from ( select AUDIT_ID,MCHT_ID,MCHT_NAME,MCHT_SIM_NAME,mcht_State,MCHT_TYPE,MCHT_NAT,PAR_MCH_ID,EXAM_PS,EMAIL from MCHT_BASE_INFO_TEMP where substr(MCHT_ID,0,15) in ( select MCHT_NO from MCHT_ORG_REL where ORG_ID = #{orgId,jdbcType=VARCHAR} and ORG_TYPE = #{orgType,jdbcType=VARCHAR} union select MCHT_NO from MCHT_ORG_REL_TEMP where ORG_ID = #{orgId,jdbcType=VARCHAR} and ORG_TYPE = #{orgType,jdbcType=VARCHAR}
)
) tmp inner join ( select * from PMP_AUDIT_STEP_INFO where (audit_datet_ime like #{auditDate,jdbcType=VARCHAR} or audit_datet_ime like #{preAuditDate,jdbcType=VARCHAR}) and audit_state in ('01','02','03') ) aud on tmp.audit_id = aud.audit_id
