mapper.select:
select 

from BACKUP_TABLE_CONFIG
where AUTO_CLEAR_FLAG = '1'

  and ORIG_TABLE_OWNER = #{origTableOwner,jdbcType=VARCHAR}



  and TABLE_NAME = #{tableName,jdbcType=VARCHAR}

mapper.delete:
delete from BACKUP_TABLE_CONFIG
where TABLE_NAME = #{tableName,jdbcType=VARCHAR}
  and ORIG_TABLE_OWNER = #{origTableOwner,jdbcType=VARCHAR}
