mapper.select:
select 

from BTH_PAGY_FILE_SUM
where PAGY_NO = #{pagyNo,jdbcType=CHAR}
  and CHK_DATA_DT = #{chkDataDt,jdbcType=CHAR}

mapper.delete:
delete from BTH_PAGY_FILE_SUM
where PAGY_NO = #{pagyNo,jdbcType=CHAR}
  and CHK_DATA_DT = #{chkDataDt,jdbcType=CHAR}

delete from BTH_PAGY_FILE_SUM
where PAGY_SYS_NO = #{pagySysNo,jdbcType=CHAR}
  and CHK_DATA_DT = #{settleDate,jdbcType=CHAR}
